mapper.select:
SELECT NOT 0 >= ( SELECT COUNT(*) FROM SAFE_DEPOSIT_BOX LEFT JOIN AWS_IAM_ROLE_PERMISSIONS ON SAFE_DEPOSIT_BOX.ID = AWS_IAM_ROLE_PERMISSIONS.SDBOX_ID LEFT JOIN ROLE ON AWS_IAM_ROLE_PERMISSIONS.ROLE_ID = ROLE.ID LEFT JOIN AWS_IAM_ROLE ON AWS_IAM_ROLE_PERMISSIONS.AWS_IAM_ROLE_ID = AWS_IAM_ROLE.ID WHERE SAFE_DEPOSIT_BOX.ID = #{sdbId} AND (AWS_IAM_ROLE.AWS_IAM_ROLE_ARN = #{iamPrincipalArn} OR AWS_IAM_ROLE.AWS_IAM_ROLE_ARN = #{iamRootArn}) AND ROLE.NAME IN
#{role}
) as HAS_PERM;

SELECT NOT 0 >= (
SELECT
COUNT(*)
FROM SAFE_DEPOSIT_BOX
LEFT JOIN AWS_IAM_ROLE_PERMISSIONS ON SAFE_DEPOSIT_BOX.ID = AWS_IAM_ROLE_PERMISSIONS.SDBOX_ID
LEFT JOIN ROLE ON AWS_IAM_ROLE_PERMISSIONS.ROLE_ID = ROLE.ID
LEFT JOIN AWS_IAM_ROLE ON AWS_IAM_ROLE_PERMISSIONS.AWS_IAM_ROLE_ID = AWS_IAM_ROLE.ID
WHERE
SAFE_DEPOSIT_BOX.ID = #{sdbId}
AND
(AWS_IAM_ROLE.AWS_IAM_ROLE_ARN = #{assumedRoleArn}
OR
AWS_IAM_ROLE.AWS_IAM_ROLE_ARN = #{iamRoleArn}
OR
AWS_IAM_ROLE.AWS_IAM_ROLE_ARN = #{iamRootArn})
AND
ROLE.NAME IN

    #{role}

) as HAS_PERM;

SELECT NOT 0 >= ( SELECT COUNT(*) FROM SAFE_DEPOSIT_BOX LEFT JOIN USER_GROUP_PERMISSIONS ON SAFE_DEPOSIT_BOX.ID = USER_GROUP_PERMISSIONS.SDBOX_ID LEFT JOIN USER_GROUP ON USER_GROUP_PERMISSIONS.USER_GROUP_ID = USER_GROUP.ID LEFT JOIN ROLE ON USER_GROUP_PERMISSIONS.ROLE_ID = ROLE.ID WHERE SAFE_DEPOSIT_BOX.ID = #{sdbId} AND USER_GROUP.NAME IN
#{userGroup}
AND ROLE.NAME IN
#{role}
) as HAS_PERMS

SELECT NOT 0 >= ( SELECT COUNT(*) FROM SAFE_DEPOSIT_BOX LEFT JOIN USER_GROUP_PERMISSIONS ON SAFE_DEPOSIT_BOX.ID = USER_GROUP_PERMISSIONS.SDBOX_ID LEFT JOIN USER_GROUP ON USER_GROUP_PERMISSIONS.USER_GROUP_ID = USER_GROUP.ID LEFT JOIN ROLE ON USER_GROUP_PERMISSIONS.ROLE_ID = ROLE.ID WHERE SAFE_DEPOSIT_BOX.ID = #{sdbId} AND UPPER(USER_GROUP.NAME) IN
UPPER(#{userGroup})
AND ROLE.NAME IN
#{role}
) as HAS_PERMS
